import "./newuser.css";
import React, { useState, useEffect } from "react";

const Test = () => {
  const [data, setData] = useState("");

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = () => {
    fetch("https://randomuser.me/api")
      .then((resp) => {
        return resp.json();
      })
      .then((res) => {
        setData(res);
      });
  };

  const refresh = () => {
    fetchData();
  };

  return (
    <div>
      <h3>Our New Member</h3>
      <div className="my_form">
        <label className="my_label">FullName</label>
        <br />
        <input id="user" type="text" placeholder="FullName" />
        <br />

        <label className="my_label">City</label>
        <br />

        <input type="text" placeholder="City" />
        <br />

        <label className="my_label">Mobile No.</label>
        <br />

        <input type="text" placeholder="Mobile" />
        <br />

        <button>Add Todo</button>
      </div>

      <div className="my_table">
        <table>
          <thead>
            <tr>
              <th>FullName</th>
              <th>City</th>
              <th>Mobile No.</th>
              <th>Salary</th>
              <th>Actions</th>
            </tr>
          </thead>

          {data &&
            data.results.map((item) => {
              return (
                <tbody>
                  <tr key={item}>
                    <td>
                      {" "}
                      {item.name.title} {item.name.first}
                      {item.name.last}
                    </td>
                    <td>{item.email}</td>
                    <td></td>
                    <td>{item.email}</td>
                  </tr>
                </tbody>
              );
            })}
        </table>
      </div>
    </div>
  );
};

export default Test;
