import "./newuser.css";
import React, { useState, useEffect } from "react";

const Test = () => {
  const [data, setData] = useState("");

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = () => {
    fetch("https://randomuser.me/api")
      .then((resp) => {
        return resp.json();
      })
      .then((res) => {
        setData(res);
      });
  };

  const refresh = () => {
    fetchData();
  };

  return (
    <div>
      <h3>Our New Member!</h3>

      <div className="my_table">
        <table>
          <thead>
            <tr>
              <th>FullName</th>

              <th>Email</th>
            </tr>
          </thead>

          {data &&
            data.results.map((item) => {
              return (
                <tbody>
                  <tr key={item}>
                    <td>
                      <h5> Welcome </h5>
                      {item.name.title} {item.name.first}
                      {item.name.last} <h5>!</h5>
                    </td>
                    <td>{item.email}</td>
                  </tr>
                </tbody>
              );
            })}
        </table>
      </div>
    </div>
  );
};

export default Test;
